import React from 'react';
import {Redirect} from 'react-router-dom'
class Home extends React.Component{
	constructor(){
		super()
		this.state={
			isLogin:true
		}


	}
logout=()=>{
	if (this.state.isLogin){
		this.setState({
			isLogin:false
		})
	}
}

render(){
	if(!this.state.isLogin){
		return <Redirect to='/login' />
	}
	return <div> 
		<h1>on home page </h1>
		<button onclick={this.logout}>Activate Lasers</button>
	</div>
}

}
export default Home;
