import React from 'react';


import {Redirect, BrowserRouter, Route, Link, Switch } from 'react-router-dom';
import Register from '../Register/Registeration.js';
import { Suspense, lazy } from 'react';

const Main = lazy(() => import('../../Main/index.js'));

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state={
          username:'',
          password:'',
          isLogin:false
        }



    }
 handleChange=(event)=> {

    this.setState({[event.target.name]: event.target.value});
  }

submitform = (e) => {
      let u_username='lalit'
      let u_password='lalit@12345'
      let username=this.state.username
      let password=this.state.password

      if(u_username==username && u_password==password){
          this.setState({
            isLogin:true
          })
      }

        }
    render() {

        if(this.state.isLogin){

          return <Redirect to='/' />
        }

            return (


          <div class="login-form">

            <h1>Login Form</h1>
            <form onSubmit={this.submitform}>
              <input type="text" name="username"  placeholder="Username" value={this.state.username} onChange={this.handleChange} required />
              <input type="text" name="password" placeholder="password" value={this.state.password}  onChange={this.handleChange} required/>
              <input type="submit" />

            </form>

      <Link to="signup">register</Link>
  </div>


    )
  }
}

export default Login;
