import React from 'react';
import  {Component}  from 'react';
import { Redirect } from 'react-router-dom';
import { BrowserRouter , Route, Link, Switch } from 'react-router-dom';


class Register extends React.Component{
	constructor(props){
		super(props)
		this.state={
			username:'',
			password:'',
			email:'',
			confirm_password:''
		}

	}





handleChange=(event)=> {

    this.setState({[event.target.name]: event.target.value});
  }

submitform=(e)=>{
	console.log(this.state)
	if(this.state.p.includes(this.state.username)){
		alert('username already taken')
	}
  e.preventDefault()
  const {username,email,password,confirm_password}=this.state

  //logic magic
  const requestOptions = {
        method: 'POST',

         headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       },
        body: JSON.stringify({ username: this.state.username,email:this.state.email,password:this.state.password,confirm_password:this.state.confirm_password})
    }


    console.log(requestOptions)
     fetch('/user', requestOptions)
        .then(response=>{
        	console.log(response)

         })

     }

render(){



	return (
		<div>
			<div>
		     <h1>Registeration Form</h1>

			      <form onSubmit={this.submitform}>
			         <input type="text" name="username"  placeholder="Username" value={this.state.username} onChange={this.handleChange} required />
			         <input type="text" name="password" placeholder="password" value={this.state.password} onChange={this.handleChange} required/>
			         <input type="email" name="email" placeholder="email" value={this.state.email} onChange={this.handleChange}  required/>
			         <input type="text" name="confirm_password"  placeholder="confirm_password" value={this.state.confirm_password} onChange={this.handleChange} required />
			         <input type="submit"/>
			      </form>

  			</div>



  			<div>
  				<Link to='/signin'>Login </Link> 
  			</div>
  		</div>
		)
}

}
export default Register;
