import React from 'react';
import {Redirect} from 'react-router-dom'
class Home extends React.Component{
	constructor(){
		super()
		this.state={
			isLogin:true,
			users:[]
		}


	}
logout=()=>{

	if (this.state.isLogin){
		localStorage.removeItem('username');
		this.setState({
			isLogin:false
		})
	}
}
delete=(user_id)=>{
	const requestOptions = {
        method: 'POST',
        headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       },
        body: JSON.stringify({user_id:user_id})
    };
	fetch('/delete',requestOptions).then(response=>console.log(response))
}
componentDidMount(){



	console.log('inside componentDidMounut')
	fetch('/user').then(response=>response.json()).then(data=>{
		console.log(data)
		this.setState({
			users:data
		})
	})
}

render(){
	const token = localStorage.getItem("username")
	if(!token){
		return <Redirect to='/signin' />
	}
	return <div> 
		<h1>on home page </h1>
		<div>
			<table border="1">
				<thead>
					<tr>
						<th>ID</th>
						<th>Username</th>
						<th>Email</th>
					</tr>
				</thead>

				<tbody>
					{this.state.users.map(user =>(
						<tr>
							<td>{user.user_id}</td>
							<td>{user.username}</td>
							<td>{user.email}</td>
							<button onClick={(e)=>{this.delete(user.user_id)}}>delete</button>
						</tr>

						))}
				</tbody>
			</table>
		</div>
		<br></br>

		<button onClick={this.logout}>Logout</button>
	</div>
}

}
export default Home;
