   constructor(props) {
        super(props);
        this.state={
          username:'',
          password:''
        }
      }

 handleChange=(event)=> {

    this.setState({[event.target.name]: event.target.value});
  }

submitform = (e) => {
      e.preventDefault()
      let username=e.target[0].value
      let password=e.target[1].value


        //logic magic
        const requestOptions = {
            method: 'POST',

            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({ username: username, password: password })
        };
        fetch('/auth', requestOptions)
            .then(response  => {
                 if(response.ok){
                    this.props.changeName(true)
                    const token=localStorage.setItem("username",username)

                   }

                })

         }
componentDidMount(){
  fetch('/test').then(response=>response.json()).then(data=>{
    console.log(data)
    this.setState({
      data:data.message
    })
  })
}

render(){
  console.log(this.state.data)
  return (
    <div className="App">
      <div className="login-form">
          <h1>Login Form</h1>
            <form onSubmit={this.submitform}>
              <input type="text" name="username"  placeholder="Username" value={this.state.username} onChange={this.handleChange} required />
              <input type="text" name="password" placeholder="password" value={this.state.password}  onChange={this.handleChange} required/>
              <input type="submit" />
            </form>


  </div>
    </div>
  );
}

}



export default App;
